dict.string:
#!/usr/bin/env bash for f in bw /usr/local/bin/bw /opt/local/bin/bw /usr/bin/bw; do if test -x $f; then bw_exec=$f fi done
export PATH=$PATH:/usr/local/bin $bw_exec config server {query} > /dev/null 2>&1
echo {query}
osascript -e 'tell application "Alfred 3" to search "bw"'

#!/bin/bash

if ! [[ $1 == "on" ]] && ! [[ $1 == "off" ]]; then
	echo "error: $1 - Neither on nor off";
	exit 1;
fi

security add-generic-password -a "alfred-bitwarden-2fa-enabled" -s "alfred-bitwarden-2fa-enabled" -w "$1" -C "note" -U

echo "$1"

osascript -e 'tell application "Alfred 3" to search "bw"'

#!/bin/bash

re='^[0-5]+$'
if ! [[ $1 =~ $re ]] || ! [[ ${#1} == "1" ]]; then
	echo "error: $1 - Not a number in range 0-5";
	exit 1;
fi

security add-generic-password -a "alfred-bitwarden-2fa-method" -s "alfred-bitwarden-2fa-method" -w "$1" -C "note" -U

echo "$1"

osascript -e 'tell application "Alfred 3" to search "bw"'

#!/bin/bash
security delete-generic-password -s alfred-bitwarden-2fa-method > /dev/null echo "{query}"
osascript -e 'tell application "Alfred 3" to search "bw"'
